    def test_indy_dict(self):
        deco_indy = AttachDecorator.from_indy_dict(self.indy_cred)
        assert deco_indy.mime_type == 'application/json'
        assert hasattr(deco_indy.data, 'base64_')
        assert deco_indy.indy_dict == self.indy_cred
